import React, { useState } from 'react'

export default function TextForm(props) {

    const handleUpClick = () => {
        // console.log(text);
        let newText = text.toUpperCase();
        setText(newText)
    }

    const handleLoClick = () => {
        // console.log(text);
        let newText = text.toLowerCase();
        setText(newText)
    }


    const handleOnChange = (e) => {
        // console.log("on change")
        setText(e.target.value)
    }

    const handleClear = () => {
        let newtext = '';
        setText(newtext);
    }

    const handleCopy = () => {
        let text = document.getElementById('mybox')
        text.select();
        navigator.clipboard.writeText(text.value)
    }

    const handleExtraSpaces = () => {
        let newText = text.split(/[ ]+/);
        setText(newText.join(' '))

    }

    const [text, setText] = useState("");
    // text is used as an input text 
    // setText is like an output text.means text got after transform 
    return (
        <>
            <div>
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    <textarea className="form-control" placeholder='Write text here' style={{ backgroundColor: props.mode === 'dark' ? 'light' : 'dark' }} onChange={handleOnChange} value={text} id="mybox" rows="8"></textarea>
                </div>
                <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert to Uppercase</button>
                <button className="btn btn-success mx-2" onClick={handleLoClick}>Convert to Lowercase</button>
                <button className="btn btn-secondary mx-2" onClick={handleClear}>Clear Text</button>
                <button className="btn btn-danger mx-2" onClick={handleCopy}>Copy Text</button>
                <button className="btn btn-warning mx-2" onClick={handleExtraSpaces}>Remove Extra space</button>
            </div>

            <div className="container  my-3">
                <h2>Your Text summary</h2>
                <p> {text.split(" ").length} <strong>words</strong> and {text.length + " "}<strong>Characters</strong></p>
            </div>
        </>
    )
}
