import React, { useState } from 'react'

export default function TextForm(props) {

    const handleUpClick = () => {
        // console.log(text);
        let newText = text.toUpperCase();
        setText(newText)
    }


    const handleOnChange = (e) => {
        // console.log("on change")
        setText(e.target.value)
    }

    const [text, setText] = useState();
    // text is used as an input text 
    // setText is like an output text.means text got after transform 
    return (
        <>
            <div>
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    <textarea className="form-control" placeholder='Write text here' onChange={handleOnChange} value={text} id="mybox" rows="8"></textarea>
                </div>
                <button className="btn btn-primary" onClick={handleUpClick}>Convert to Uppercase</button>
            </div>

            <div className="container">
                <h1>Your Text summary</h1>
            </div>
        </>
    )
}
